import React from "react";
import html from "../assets/experience/html.png";
import css from "../assets/experience/css.png";
import javascript from "../assets/experience/javascript.png";
import reactImage from "../assets/experience/react.png";
import nextjs from "../assets/experience/nextjs.png";
import tailwind from "../assets/experience/tailwind.png";
import graphql from "../assets/experience/graphql.png";
import node from "../assets/experience/node.png";


const Experience = () => {
    const experience = [
      {
        id: 1,
        title: "HTML",
        src: html,
      },
      {
        id: 2,
        title: "CSS",
        src: css,
      },
      {
        id: 3,
        title: "JavaScript",
        src: javascript,
      },
      {
        id: 4,
        title: "React",
        src: reactImage,
      },
      {
        id: 5,
        title: "Next JS",
        src: nextjs,
      },
      {
        id: 6,
        title: "Tailwind",
        src: tailwind,
      },
      {
        id: 7,
        title: "GraphQL",
        src: graphql,
      },
      {
        id: 8,
        title: "Node JS",
        src: node,
      },
    ];
  
    return (
      <div id="experience" className="w-full">
        <div className="max-w-screen-xl mx-auto px-8 py-16 text-center md:text-left">
          <p className="text-5xl md:text-7xl tracking-wider uppercase text-blue-500 font-bold">
            Experience
          </p>
          <h2 className="py-4 max-w-lg">
            Entrance gloating i nothing soul quoth desolate. My he bird still just
            of theeby lattice. Nearly ominous leave god is.
          </h2>
          <div className="grid lg:grid-cols-4 gap-8">
            {experience.map(({ id, title, src }) => (
              <div
                key={id}
                className="flex flex-col lg:flex-row gap-10 lg:gap-0 items-center justify-between p-6 shadow-lg odd:shadow-blue-400 even:shadow-rose-400 rounded-xl hover:scale-105 ease-in duration-300"
              >
                <image src={src} width="64px" height="64px" alt="randomness" />
                <h3 className="font-light">{title}</h3>
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  };
  
  export default Experience;
