import React,{ useState } from 'react'
import {FaBars, FaTimes } from "react-icons/fa"

const NavBar = () => {
  const [ nav, setNav ] = useState(false);
  return (
    <div className="flex justify-between items-center w-full h-20 text-white bg-black px-4 fixed">
    <div>
      <h1 className="text-3xl ml-2" ><MdKeyboardArrowRight />ASHISH SINGH</h1>
    </div>
    <ul className="hidden md:flex ">
      <li className="px-4 cursor-pointer font-medium text-gray-500 hover:scale-105 duration-200">Home</li>
      <li className="px-4 cursor-pointer font-medium text-gray-500 hover:scale-105 duration-200">About</li>
      <li className="px-4 cursor-pointer font-medium text-gray-500 hover:scale-105 duration-200">Portfolio</li>
      <li className="px-4 cursor-pointer font-medium text-gray-500 hover:scale-105 duration-200">Expreince</li>
      <li className="px-4 cursor-pointer font-medium text-gray-500 hover:scale-105 duration-200">Contact</li>
    </ul>
    <div onClick={() => setNav (!nav)} className="cursoe-pointer p-4 z-10 text-gray-500 md:hidden ">
    {nav? <FaTimes size={30} /> : <FaBars size={30} />}

      </div>
      {nav && (
      <ul className="flex flex-col justify-center items-center absolute top-0
left-0 w-full h-screen bg-gradient-to-b from-black to-gray-800
text-gray-500">
  <li className="px-4 cursor-pointer capitalize py-6 text-4xl ">Home</li>
  <li className="px-4 cursor-pointer capitalize py-6 text-4xl ">About</li>
  <li className="px-4 cursor-pointer capitalize py-6 text-4xl ">Portfolio</li>
  <li className="px-4 cursor-pointer capitalize py-6 text-4xl ">Expreince</li>
  <li className="px-4 cursor-pointer capitalize py-6 text-4xl ">Contact</li>
</ul>
 )}
      </div>
  );
};

export default NavBar;
