import React, { useEffect, useRef } from 'react';
import { MdKeyboardArrowRight } from 'react-icons/md';
import image from '../assets/image.jpg';

const Home = () => {
  useEffect(() => {
    const requestAnimFrame =
      window.requestAnimationFrame ||
      window.webkitRequestAnimationFrame ||
      window.mozRequestAnimationFrame ||
      window.oRequestAnimationFrame ||
      window.msRequestAnimationFrame ||
      function (callback) {
        window.setTimeout(callback);
      };

    const init = (elemid) => {
      let canvas = document.getElementById(elemid);
      let c = canvas.getContext('2d');
      let w = (canvas.width = window.innerWidth);
      let h = (canvas.height = window.innerHeight);
      c.fillStyle = 'rgba(30,30,30, 1)';
      c.fillRect(0, 0, w, h);
      return { c, canvas };
    };

    const animate = () => {
      let { c, canvas } = init('canvas');
      let w = (canvas.width = window.innerWidth);
      let h = (canvas.height = window.innerHeight);

      // Your animation logic here

      requestAnimFrame(animate);
    };

    animate();
  }, []);

  return (
    <div name="home" className="h-screen w-full bg-gradient-to-b from-black via-black to-gray-800">
      <div className="max-w-screen-lg mx-auto flex flex-col items-center justify-center h-full px-4 md:flex-row">
        <div className="flex flex-col justify-center h-full">
          <h2 className="text-4xl sm:text-7xl font-bold text-white">I'm a Fullstack Developer</h2>
          <p className="text-gray-500 py-4 max-w-md">
            As a full stack developer, I have a strong passion for building innovative and efficient web applications. With expertise in both front-end and back-end technologies, I am able to seamlessly integrate and optimize various components of a project.
          </p>

          <div>
            <button className="text-white w-fit px-6 py-3 my-2 flex items-center rounded-md bg-gradient-to-r from-cyan-500 to-blue-500">
              Portfolio<span><MdKeyboardArrowRight /> </span>
            </button>
          </div>
        </div>
        <div>
          <img src={image} alt="my profile" className="rounded-2xl mx-auto w-2/3 md:w-full" />
        </div>
      </div>
      <canvas id="canvas" ref={useRef(null)}></canvas>
    </div>
  );
};

export default Home;
