import React from "react";
import html from "../assets/experience/html.png";
import css from "../assets/experience/css.png";
import javascript from "../assets/experience/javascript.png";
import reactImage from "../assets/experience/react.png";
import nextjs from "../assets/experience/nextjs.png";
import tailwind from "../assets/experience/tailwind.png";
import graphql from "../assets/experience/graphql.png";
import node from "../assets/experience/node.png";

const Experience = () => {
  return (
    <div name="experience" className="bg-gradient-to-b from-gray-800 to-black w-full h-screen" >
      <div className="max-w-screen-lg mx-auto p-4 flex flex-col justify-center w-full h-full text-white">
        <div>
            <p className="text-4xl font-bold border-b-4 border-gray-500 p-2 inline">Experience</p>
            <p className="py-6">This are the technologies I'have worked with</p>
        </div>
        <div className="w-full gird grid-cols2 sm:grid-cols-3 gap-8 text-center py-4 px12 sm:px-0">
            <div className="shadow-md  shadow-yellow-800 hoverscale-105 duration-500 py-6 rounded-lg">
            <img src={html} alt="" className="w-20 mx-auto"/>
            <p className="mt-4">HTML</p>
            </div>
        </div>
      </div>
    </div>
  )
}

export default Experience
