import React from 'react';

const About = () => {
  const titleStyle = `
    h1 .before-effect::before {
      transform: scaleX(0);
      transform-origin: bottom right;
    }

    h1:hover .before-effect::before {
      transform: scaleX(1);
      transform-origin: bottom left;
    }

    h1 .before-effect::before {
      content: " ";
      display: block;
      position: absolute;
      top: 0;
      right: 0;
      bottom: 0;
      left: 0;
      inset: 0 0 0 0;
      background: hsl(200 100% 80%);
      z-index: -1;
      transition: transform 0.3s ease;
    }

    h1 {
      position: relative;
      font-size: 5rem;
    }

    html {
      block-size: 100%;
      inline-size: 100%;
    }

    body {
      min-block-size: 100%;
      min-inline-size: 100%;
      margin: 0;
      box-sizing: border-box;
      display: grid;
      place-content: center;
      font-family: system-ui, sans-serif;
    }

    @media (orientation: landscape) {
      body {
        grid-auto-flow: column;
      }
    }
  `;

  return (
    <div name="about" className="w-full h-screen bg-gradient-to-b from-gray-800 to-black text-white" style={{ paddingTop: "200px" }}>
      <div className="max-w-screen-lg p-4 mx-auto flex flex-col justify-center w-full h-full">
        <div className="pb-8">
          <h1 className="text-4xl font-bold inline border-b-4 border-gray-500">
            <span className="before-effect">About</span>
          </h1>
        </div>
        <p className="text-xl mt-10">
          As a UI/UX and AR filter developer, I craft websites from scratch, seamlessly aligning elements with the user journey. Proficient in Figma and WordPress, I create visually appealing, intuitive interfaces. I also design captivating 3D objects and AR filters for Facebook, Instagram, and Snapchat, focusing on immersive experiences. Passionate about design's impact, I'm based in Surat, Gujarat, continuously learning to deliver unique projects that delight users. Attention to detail ensures user satisfaction, making each experience memorable.
        </p>
        <br />
        <style>{titleStyle}</style>
      </div>
    </div>
  );
};

export default About;
